%%%% Métodos de TaylorT=10;x0=0;y0=0;N=200;f=@(x,y)(1-sin(y));solex=@(x)(2*acos((2+x)./sqrt(4+4*x+2*x.^2)));f2=@(x,y)(-cos(y).*(1-sin(y)));xs=linspace(x0,T,N+1);lyE = MetEuler(f,x0,y0,T, N);lyT = MetTaylordem2(f,f2,x0,y0,T,N); hold onxsparagrafico=linspace(x0,T,200);plot(xsparagrafico,solex(xsparagrafico),'-b','linewidth',3);plot(xs,lyE,'*r','MarkerSize',11)plot(xs,lyT,'.k','MarkerSize',16)legend('Solucao exata','Apr. Metodo de Euler'... ,'Apr. Metodo de Taylor de ordem 2'...,'Location','NorthWest')hold onse=solex(xs);plot(xs,abs(se-lyE),'*r','MarkerSize',11)plot(xs,abs(se-lyT),'.k','MarkerSize',16)legend('Erro Abs. Metodo de Euler'... ,'Erro Abs. Metodo de Taylor de ordem 2'...,'Location','NorthWest')%%% Métodos de Runge-KuttaT=2;x0=0;y0=1;N=40;f=@(x,y)(-y+2*sin(4*x));solex=@(x)(-(1/17)*exp(-x).*(-25 + 8*exp(x).*cos(4*x) - 2*exp(x).*sin(4*x)));solexata=solex(T);xs=linspace(x0,T,N+1);lyE = MetEuler(f,x0,y0,T, N);lyRKpm = MetRKHeun(f,x0,y0,T, N); lyRKem = MetRKEulerModificado(f,x0,y0,T, N);lyRK4 = MetRK4(f,x0,y0,T, N);hold onxsparagrafico=linspace(x0,T,200);plot(xsparagrafico,solex(xsparagrafico),'-b','linewidth',3);plot(xs,lyE,'*','MarkerSize',11)plot(xs,lyRKpm,'ogreen','MarkerSize',11)plot(xs,lyRKem,'diamond black','MarkerSize',11)plot(xs,lyRK4,'.k','MarkerSize',16)legend('Solucao exata','Apr. Metodo de Euler'... ,'Apr. Metodo de RK - Heun','Apr. Metodo de RK Euler Modificado','Apr. Metodo de RK de ordem 4'...,'Location','SouthWest')%%%% EulerNs=500:10:2000;hs=T./Ns;aproximacoeseuler=zeros(1,numel(Ns));for k=1:numel(Ns)ly = MetEuler(f,x0,y0,T, Ns(k));aproximacoeseuler(k)=ly(end);enderroabseuler=abs(aproximacoeseuler-solexata);polyfit(log(hs),log(erroabseuler),1)%%%% HeunNs=500:10:2000;hs=T./Ns;aproximacoesRKpm=zeros(1,numel(Ns));for k=1:numel(Ns)ly = MetRKHeun(f,x0,y0,T, Ns(k));aproximacoesRKpm(k)=ly(end);enderroabsRKpm=abs(aproximacoesRKpm-solexata);polyfit(log(hs),log(erroabsRKpm),1)%%% Euler modificadoNs=5000:10:10000;hs=T./Ns;aproximacoesRKem=zeros(1,numel(Ns));for k=1:numel(Ns)ly = MetRKEulerModificado(f,x0,y0,T, Ns(k));aproximacoesRKem(k)=ly(end);enderroabsRKem=abs(aproximacoesRKem-solexata);polyfit(log(hs),log(erroabsRKem),1)%%% RK de ordem 4Ns=1000:10:3000;hs=T./Ns;aproximacoesRK4=zeros(1,numel(Ns));for k=1:numel(Ns)ly = MetRK4(f,x0,y0,T, Ns(k));aproximacoesRK4(k)=ly(end);enderroabsRK4=abs(aproximacoesRK4-solexata);polyfit(log(hs),log(erroabsRK4),1)T=2;x0=1;y0=1;N=2;f=@(x,y)(x.*sin(y));solex=@(x)(2*acot(exp(1/2 - x.^2/2)*cot(1/2)));lyE = MetEuler(f,x0,y0,T, N);lyRKpm = MetRKHeun(f,x0,y0,T, N); lyRKem = MetRKEulerModificado(f,x0,y0,T, N);lyRK4 = MetRK4(f,x0,y0,T, N);hold onxsparagrafico=linspace(x0,T,200);xs=linspace(x0,T,N+1);plot(xsparagrafico,solex(xsparagrafico),'-b','linewidth',3);plot(xs,lyE,'*','MarkerSize',11)plot(xs,lyRKpm,'ogreen','MarkerSize',11)plot(xs,lyRKem,'diamond black','MarkerSize',11)plot(xs,lyRK4,'.k','MarkerSize',16)legend('Solucao exata','Apr. Metodo de Euler'... ,'Apr. Metodo de RK - Heun','Apr. Metodo de RK Euler Modificado','Apr. Metodo de RK de ordem 4'...,'Location','NorthWest')